#include <stdio.h>
#include <string>
#include <unordered_set>
#include <iostream>
#include <vector>
//#include <cmath>

using namespace std;

class DistinctSubsequence {
public:
    
    int numDistinctOptimization(string s, string t) {
        int n = (int)s.length(), k = (int)t.length();
        
        // empty or full case
        if (n == 0 || k == 0 || n < k) { return 0; }
        else if (n == k) { return s == t ? 1 : 0; }
        
        // check target char validness
        unordered_set<char> validChars{s[0]};
        for (char c: s) { validChars.insert(c); }
        for (char c: t) { if (validChars.find(c) == validChars.end()) { return 0; } }
        
        // initialization: vector因为构造函数的缘故使用起来简单得多
        vector<int> path(k+1, 0);
        path[0] = 1;
        
        // dp (里圈倒着数是为了怕后一次干扰前一次）
        for (int i = 1; i <= n; ++i) {
            for (int j = 1 + min(k, i); j > 0; --j) { path[j] = path[j] + ((s[i-1] == t[j-1]) ? path[j-1] : 0); }
        }
        
        
        return path[k];
    }
    
    int numDistinct(string s, string t) {
        int n = (int)s.length(), k = (int)t.length();
        
        // empty or full case
        if (n == 0 || k == 0 || n < k) { return 0; }
        else if (n == k) { return s == t ? 1 : 0; }
        
        // check target char validness
        unordered_set<char> validChars{s[0]};
        for (char c: s) { validChars.insert(c); }
        for (char c: t) { if (validChars.find(c) == validChars.end()) { return 0; } }
        
        // initialization
        int f[n][k]; // f[i][j] is the count s.substring(0, i+1) contains t.substring(0, j+1) (no need to be consecutive)
        for (int i = 0; i < n; ++i) { // c++与java, c#不同，不会给数组元素赋上默认值，也就是说默认的f[i][j]不一定等于零
            for (int j = 1; j < k; ++j) { f[i][j] = 0; }
        }
        f[0][0] = (s[0]==t[0]);
        
        // dp
        for (int i = 1; i < n; ++i) {
            f[i][0] = f[i-1][0] + ((s[i] == t[0]) ? 1 : 0);
            for (int j = 1; j < k && j <= i; ++j) { f[i][j] = f[i-1][j] + ((s[i] == t[j]) ? f[i-1][j-1] : 0); }
            //这里存在很明显的化二维为一维的特征：i只和i-1, i-1有关。j只和j, j-1有关。这种情况下通常是把里层保留，外层去掉， 因为外层不会被重复计算。
        }
        
        
        return f[n-1][k-1];
    }
};

int main() {
//    string s = "";
//    string t = "rwmimatmhydhbujebqehjprarwfkoebcxxqfktayaaeheys";
//    string s = "ccc", t = "c";
//    string s = "ddd", t = "dd";
    string s = "aabb", t = "ab";
    DistinctSubsequence sol;
    cout << (sol.numDistinctOptimization(s, t)) << endl;
}